<div class="w-full md:w-1/2 md:pl-10">
  <p class="text-center text-lg font-bold sm:text-left">Get early access</p>
  <form action="#">
    <input class="my-8 block w-full rounded-sm border border-gray-300 bg-gray-50 p-4 text-sm text-gray-900 focus:border-blue-500 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-700 dark:text-white dark:placeholder-gray-400 dark:focus:border-blue-500 dark:focus:ring-blue-500" id="first_name" type="text" placeholder="First name" required />
    <input class="my-8 block w-full rounded-sm border border-gray-300 bg-gray-50 p-4 text-sm text-gray-900 focus:border-blue-500 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-700 dark:text-white dark:placeholder-gray-400 dark:focus:border-blue-500 dark:focus:ring-blue-500" id="last_name" type="text" placeholder="Last name" required />
    <input class="my-8 block w-full rounded-sm border border-gray-300 bg-gray-50 p-4 text-sm text-gray-900 focus:border-blue-500 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-700 dark:text-white dark:placeholder-gray-400 dark:focus:border-blue-500 dark:focus:ring-blue-500" id="email" type="email" placeholder="Email address" required />
    <input class="my-8 block w-full rounded-sm border border-gray-300 bg-gray-50 p-4 text-sm text-gray-900 focus:border-blue-500 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-700 dark:text-white dark:placeholder-gray-400 dark:focus:border-blue-500 dark:focus:ring-blue-500" id="password" type="password" placeholder="Password" required />
    <input class="my-8 block w-full rounded-sm border border-gray-300 bg-gray-50 p-4 text-sm text-gray-900 focus:border-blue-500 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-700 dark:text-white dark:placeholder-gray-400 dark:focus:border-blue-500 dark:focus:ring-blue-500" id="phone_select" type="text" placeholder="Phone number" required />

    <button class="bg-primary mb-10 mt-6 w-full rounded-md py-2 capitalize text-white shadow-lg">login</button>
    <p class="text-center text-slate-500">Don't have an Account? <a class="color-primary font-semibold" href="/signup">Sign Up</a></p>
  </form>
</div>
